sys_variable_value.value:
(function execute(inputs, outputs) {
	var emails = [];
	while(inputs.allUserGroup.next()){
		emails.push(allUserGroup.user.email);
	}
	outputs.allEmails = emails.join(',');
})(inputs, outputs);

